    try {
      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };
      //destructuring original => res.data
      const { data } = await axios.post(
        `http://localhost:9090/api/elearning/chaptersdata/${cId}`,
        config
      );

      setCourseData(data);
      console.log(data);
